import numpy as npimport huracanpydef test_add_all_info():    data = huracanpy.load(huracanpy.example_csv_file)    data = huracanpy.info.add_all_info(data)    assert (data.hemisphere == ["S"] * len(data.record)).all()    assert (data.basin == np.array(["AUS"] * 51 + ["SI"] * 48)).all()    assert (data.is_ocean == np.array([True] * 15 + [False] * 15 + [True] * 69)).all()    assert (data.country == np.array([""] * 15 + ["Australia"] * 15 + [""] * 69)).all()    assert (data.continent == np.array([""] * 15 + ["Oceania"] * 15 + [""] * 69)).all()    assert (data.season == [1980.0] * len(data.record)).all()