import numpy as npimport huracanpydef test_add_all_info():    data = huracanpy.load(huracanpy.example_csv_file)    data = huracanpy.utils.add_all_info(data, slp_units="Pa")    assert (data.hemisphere == ["S"] * len(data.record)).all()    assert (data.basin == np.array(["AUS"] * 51 + ["SI"] * 48)).all()    assert (        data.is_ocean == np.array(["Ocean"] * 15 + ["Land"] * 15 + ["Ocean"] * 69)    ).all()    assert (data.country == np.array([""] * 15 + ["Australia"] * 15 + [""] * 69)).all()    assert (data.continent == np.array([""] * 15 + ["Oceania"] * 15 + [""] * 69)).all()    assert (data.season == ["19791980"] * len(data.record)).all()    assert data.sshs.min() == -1    assert data.sshs.max() == 0    assert data.pres_cat.min() == -1    assert data.pres_cat.max() == 2.0